import { useState } from "react";
import React, { Component } from 'react'

const handleFilterChange = (event) => {
    event.preventDefault()
    setFilterAll(event.target.value)
}

const handleFilterSearch = (event) => {
    event.preventDefault()
    setFilterPersons(filterAll === "" ? persons : persons.filter(person => person.name.includes(event.filterAll)))
}


const Filter = (props) => {
    return(
        <div>
            Filter by: <input value={props.filterAll} onChange={handleFilterChange} />
            <button type='submit' onClick={handleFilterSearch}>Search</button>
        </div>
    )
}

export default Filter
